class App extends React.Component{
  state = {
    persons: [
      {name: 'Anvika', age: 0.6},
      {name: 'Advika', age: 0.6}
    ]
  }

  switchNameHandler = (newName) => {
    this.setState({
      persons: [
        {name: newName, age: 0.6},
        {name: 'Advika Verma', age: 0.6}
      ]
    })
  }

  render(){
    return(
      <div className="App">
        <h1>Hi! I am React App</h1>
        <p>This is really working</p>
        <button onClick={() => this.switchNameHandler('Annu Verma!!')}>Switch Name</button>
        <Person 
        click={this.switchNameHandler.bind(this, 'Annu!')} 
        name={this.state.persons[0].name} age={this.state.persons[0].age} >I am very naughty</Person>
        <Person name={this.state.persons[1].name} age={this.state.persons[1].age} />
      </div>
    )
  }
}

export default App;
